mapper.select:
select
from pro_tender where 1=1
and id = #{id,jdbcType=INTEGER}

select
from pro_tender a left join enterprise b on a.project_enter_id=b.id left join enterprise c on a.tender_enter_id=c.id where 1=1
and a.id = #{id,jdbcType=INTEGER}
and b.enterprise_name like concat('%',#{companyName,jdbcType=VARCHAR},'%')
and a.project_name like concat('%',#{proName,jdbcType=VARCHAR},'%')
and a.project_type like concat('%',#{tenderType,jdbcType=VARCHAR},'%')
and a.tender_amount >=#{tenderProAmtStart, jdbcType=VARCHAR}
and a.tender_amount <=#{tenderProAmtEnd, jdbcType=VARCHAR}
and a.tender_shoot_date >=concat(#{tenderShootTimeStart, jdbcType=VARCHAR},' 00:00:00')
and a.tender_shoot_date <= concat(#{tenderShootTimeEnd, jdbcType=VARCHAR}, ' 23:59:59')

select

from pro_tender a left join enterprise b on a.project_enter_id=b.id
where 1=1

    and a.id = #{id,jdbcType=INTEGER}


    and b.enterprise_name like concat('%', #{enterpriseOption, jdbcType=VARCHAR}, '%')


    and (a.project_name like concat('%', #{projectOption, jdbcType=VARCHAR}, '%')
    or a.build_scale like concat('%', #{projectOption, jdbcType=VARCHAR}, '%')
    or a.tender_amount= #{projectOption, jdbcType=VARCHAR})


    and a.tender_shoot_date >= concat(#{shootTimeStart, jdbcType=VARCHAR}, ' 00:00:00')


    and a.tender_shoot_date <= concat(#{shootTimeEnd, jdbcType=VARCHAR}, ' 23:59:59')


    and a.tender_amount >= #{tenderAmtStart, jdbcType=VARCHAR}


    and a.tender_amount <= #{tenderAmtEnd, jdbcType=VARCHAR}


    and a.tender_build_area >= #{projectScaleStart, jdbcType=VARCHAR}


    and a.tender_build_area <= #{projectScaleEnd, jdbcType=VARCHAR}
